-- Update data

ALTER TABLE `wD_WORLD_Territories` ADD `mapID` TINYINT UNSIGNED NOT NULL FIRST ,
	ADD `id` SMALLINT UNSIGNED NOT NULL AFTER `mapID`,
	ADD `name` VARCHAR( 120 ) NOT NULL AFTER `id`;
UPDATE wD_WORLD_Territories SET mapID=2,id=terr+0,name=terr;
ALTER TABLE `wD_WORLD_Territories` DROP `terr` ;

ALTER TABLE `wD_WORLD_Borders` ADD `mapID` TINYINT UNSIGNED NOT NULL FIRST,
	ADD `fromTerrID` SMALLINT UNSIGNED NOT NULL AFTER `fromTerr`,
	ADD `toTerrID` SMALLINT UNSIGNED NOT NULL AFTER `toTerr`;
ALTER TABLE `wD_WORLD_CoastalBorders` ADD `mapID` TINYINT UNSIGNED NOT NULL FIRST,
	ADD `fromTerrID` SMALLINT UNSIGNED NOT NULL AFTER `fromTerr`,
	ADD `toTerrID` SMALLINT UNSIGNED NOT NULL AFTER `toTerr`;

UPDATE wD_WORLD_Borders SET mapID=2, fromTerrID=fromTerr+0, toTerrID=toTerr+0;
UPDATE wD_WORLD_CoastalBorders SET mapID=2, fromTerrID=fromTerr+0, toTerrID=toTerr+0;

ALTER TABLE `wD_WORLD_Borders` DROP fromTerr, DROP toTerr;
ALTER TABLE `wD_WORLD_CoastalBorders` DROP fromTerr, DROP toTerr;

ALTER TABLE `wD_WORLD_Borders` ADD INDEX `fromTo` ( `fromTerrID` , `toTerrID` ), ADD INDEX `toFrom` ( `toTerrID` , `fromTerrID` );
ALTER TABLE `wD_WORLD_CoastalBorders` ADD INDEX `fromTo` ( `fromTerrID` , `toTerrID` ), ADD INDEX `toFrom` ( `toTerrID` , `fromTerrID` );
ALTER TABLE `wD_WORLD_Territories` ADD PRIMARY KEY ( `mapID` , `id` );

ALTER TABLE wD_WORLD_Territories ADD `countryID` TINYINT UNSIGNED NOT NULL AFTER country;
UPDATE wD_WORLD_Territories SET countryID=country-1;
ALTER TABLE wD_WORLD_Territories DROP `country` ;

ALTER TABLE `wD_WORLD_Territories` ADD `coastParentID` SMALLINT UNSIGNED NOT NULL;
UPDATE wD_WORLD_Territories SET coastParentID = id;
UPDATE wD_WORLD_Territories a INNER JOIN wD_WORLD_Territories b ON ( REPLACE(REPLACE(a.name, ' (North Coast)', ''), ' (South Coast)', '')=b.name ) SET a.coastParentID=b.id;
ALTER TABLE `wD_WORLD_Territories` CHANGE `coast` `coast` ENUM( 'No', 'Parent', 'Child' ) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL;
UPDATE wD_WORLD_Territories SET coast='Child' WHERE NOT (coast='No' OR coast='Parent');

-- Sync data
DROP TABLE IF EXISTS idSwaps;
CREATE TABLE idSwaps SELECT w.id as fromID, t.id as toID FROM wD_WORLD_Territories w INNER JOIN wD_Territories t ON (t.mapID=w.mapID AND t.name=w.name) WHERE NOT t.id=w.id;
ALTER TABLE `wD_WORLD_Territories` DROP PRIMARY KEY;

UPDATE wD_WORLD_Territories wt INNER JOIN idSwaps swp ON (wt.id=swp.fromID) SET wt.id=swp.toID;
UPDATE wD_WORLD_Territories wt INNER JOIN idSwaps swp ON (wt.coastParentID=swp.fromID) SET wt.coastParentID=swp.toID;
UPDATE wD_WORLD_Borders b INNER JOIN idSwaps swp ON (b.fromTerrID=swp.fromID) SET b.fromTerrID=swp.toID;
UPDATE wD_WORLD_Borders b INNER JOIN idSwaps swp ON (b.toTerrID=swp.fromID) SET b.toTerrID=swp.toID;
UPDATE wD_WORLD_CoastalBorders b INNER JOIN idSwaps swp ON (b.fromTerrID=swp.fromID) SET b.fromTerrID=swp.toID;
UPDATE wD_WORLD_CoastalBorders b INNER JOIN idSwaps swp ON (b.toTerrID=swp.fromID) SET b.toTerrID=swp.toID;

DROP TABLE idSwaps;

